actionConfiguration.body:
() => {
  if (Area.selectedOptionValue === null || Area.selectedOptionValue === '') {
    System.setSelectedOption('');
    System.setDisabled(true);
  } else {
    var SystemsData = GetSystems.data.filter(word => word.areaid.toString().includes(Area.selectedOptionValue)).map(p => ({
      label: p.system,
      value: p.id
    }));
    System.setOptions(SystemsData);
    System.setDisabled(false);
  }
  return true;
}

