actionConfiguration.body:
async () => {
  var schedules = await GetSchedules.run();
  const days = ['sunday', 'monday', 'tuesday', 'wednesday', 'thursday', 'friday', 'saturday'];
  var areaId = Area.selectedOptionValue;
  var start = new Date(forecastStart.selectedDate);
  var startWeekDay = days[start.getDay()];
  var startHour = start.getHours();
  var hasScheduleStart = false;
  if (startWeekDay === 'monday') hasScheduleStart = schedules.filter(a => a.hour === startHour.toString() + 'h' && a.monday === areaId);
  if (startWeekDay === 'tuesday') hasScheduleStart = schedules.filter(a => a.hour === startHour.toString() + 'h' && a.tuesday === areaId);
  if (startWeekDay === 'wednesday') hasScheduleStart = schedules.filter(a => a.hour === startHour.toString() + 'h' && a.wednesday === areaId);
  if (startWeekDay === 'thursday') hasScheduleStart = schedules.filter(a => a.hour === startHour.toString() + 'h' && a.thursday === areaId);
  if (startWeekDay === 'friday') hasScheduleStart = schedules.filter(a => a.hour === startHour.toString() + 'h' && a.friday === areaId);
  if (startWeekDay === 'saturday') hasScheduleStart = schedules.filter(a => a.hour === startHour.toString() + 'h' && a.saturday === areaId);
  if (startWeekDay === 'sunday') hasScheduleStart = schedules.filter(a => a.hour === startHour.toString() + 'h' && a.sunday === areaId);
  var end = new Date(forecastEnd.selectedDate);
  var endWeekDay = days[end.getDay()];
  var endHour = end.getHours();
  var hasScheduleEnd = false;
  if (endWeekDay === 'monday') hasScheduleEnd = schedules.filter(a => a.hour === endHour.toString() + 'h' && a.monday === areaId);
  if (endWeekDay === 'tuesday') hasScheduleEnd = schedules.filter(a => a.hour === endHour.toString() + 'h' && a.tuesday === areaId);
  if (endWeekDay === 'wednesday') hasScheduleEnd = schedules.filter(a => a.hour === endHour.toString() + 'h' && a.wednesday === areaId);
  if (endWeekDay === 'thursday') hasScheduleEnd = schedules.filter(a => a.hour === endHour.toString() + 'h' && a.thursday === areaId);
  if (endWeekDay === 'friday') hasScheduleEnd = schedules.filter(a => a.hour === endHour.toString() + 'h' && a.friday === areaId);
  if (endWeekDay === 'saturday') hasScheduleEnd = schedules.filter(a => a.hour === endHour.toString() + 'h' && a.saturday === areaId);
  if (endWeekDay === 'sunday') hasScheduleEnd = schedules.filter(a => a.hour === endHour.toString() + 'h' && a.sunday === areaId);
  if (hasScheduleStart.length > 0 && hasScheduleEnd.length > 0 && appsmith.store.riskGrade < 3) {
    TypeChangeRequest.setSelectedOption(1);
    TxtModal.setText("Este registro de mudança será aprovado automaticamente por estar em dia e horário previamente acordado. Deseja confirmar?");
    TxtModal.setTextColor("green");
    storeValue("typeChangeRequest", 1);
  } else {
    TypeChangeRequest.setSelectedOption(2);
    TxtModal.setText("Este registro de mudança deverá ser moderado por algum responsável. Deseja confirmar?");
    TxtModal.setTextColor("red");
    storeValue("typeChangeRequest", 2);
  }
  return true;
}

