actionConfiguration.body:
async () => {
  const changeRequestId = appsmith.store.changeRequest.id;
  await UpdateStatusChangeRequest.run({
    changeRequestId: changeRequestId,
    status: 3
  });
  var idAssessor = await GetIDAssessor.run();
  await SaveAssessment.run({
    changeRequestId: changeRequestId,
    status: 3,
    idAssessor: idAssessor[0].id
  });
  closeModal('MdlChangeRequestDetail');
  removeValue('changeRequest');
  showModal('MdlResult');
  TxtTitleMdlResult.setText("Aprovação realizada com sucesso!");
  Util.reset();
}

