		var startWeekDay = days[start.getDay()];
		var startHour = start.getHours();
		var hasScheduleStart = false;

		if (startWeekDay === 'monday')
			 hasScheduleStart = schedules.filter(a => a.hour === startHour.toString() + 'h' && a.monday  === areaId);	
		if (startWeekDay === 'tuesday')
			 hasScheduleStart = schedules.filter(a => a.hour === startHour.toString() + 'h' && a.tuesday  === areaId);
		if (startWeekDay === 'wednesday')
			 hasScheduleStart = schedules.filter(a => a.hour === startHour.toString() + 'h' && a.wednesday  === areaId);
		if (startWeekDay === 'thursday')
			 hasScheduleStart = schedules.filter(a => a.hour === startHour.toString() + 'h' && a.thursday  === areaId);
		if (startWeekDay === 'friday')
			 hasScheduleStart = schedules.filter(a => a.hour === startHour.toString() + 'h' && a.friday  === areaId);
		if (startWeekDay === 'saturday')
			 hasScheduleStart = schedules.filter(a => a.hour === startHour.toString() + 'h' && a.saturday  === areaId);
			if (startWeekDay === 'sunday')
			 hasScheduleStart = schedules.filter(a => a.hour === startHour.toString() + 'h' && a.sunday  === areaId);

		var end = new Date(forecastEnd.selectedDate);
		var endWeekDay = days[end.getDay()];
		var endHour = end.getHours();

		var hasScheduleEnd = false;

		if (endWeekDay === 'monday')
			 hasScheduleEnd = schedules.filter(a => a.hour === endHour.toString() + 'h' && a.monday  === areaId);	
		if (endWeekDay === 'tuesday')
			 hasScheduleEnd = schedules.filter(a => a.hour === endHour.toString() + 'h' && a.tuesday  === areaId);
		if (endWeekDay === 'wednesday')
			 hasScheduleEnd = schedules.filter(a => a.hour === endHour.toString() + 'h' && a.wednesday  === areaId);
		if (endWeekDay === 'thursday')
			 hasScheduleEnd = schedules.filter(a => a.hour === endHour.toString() + 'h' && a.thursday  === areaId);
		if (endWeekDay === 'friday')
			 hasScheduleEnd = schedules.filter(a => a.hour === endHour.toString() + 'h' && a.friday  === areaId);
		if (endWeekDay === 'saturday')
			 hasScheduleEnd = schedules.filter(a => a.hour === endHour.toString() + 'h' && a.saturday  === areaId);
			if (endWeekDay === 'sunday')
			 hasScheduleEnd = schedules.filter(a => a.hour === endHour.toString() + 'h' && a.sunday  === areaId);

		if (hasScheduleStart.length > 0 && hasScheduleEnd.length > 0 && appsmith.store.riskGrade < 3)
		{
			TypeChangeRequest.setSelectedOption(1);
			TxtModal.setText("Este registro de mudança será aprovado automaticamente por estar em dia e horário previamente acordado. Deseja confirmar?");
			TxtModal.setTextColor("green");
			storeValue("typeChangeRequest",1);
		}
		else
		{
			TypeChangeRequest.setSelectedOption(2);
			TxtModal.setText("Este registro de mudança deverá ser moderado por algum responsável. Deseja confirmar?");
			TxtModal.setTextColor("red");
  		storeValue("typeChangeRequest",2);
		}	
    return (true);
 },
		clearMessage: () => {
		TxtModal.setText("");
		return (true);
		},
	EnableEditItems: () => {
		if (Reason.selectedOptionValue === 1)
		{
			TxtBugs.setDisabled(true);
			TxtUserStories.setDisabled(false);
		}
		else if (Reason.selectedOptionValue === 2)
		{
			TxtBugs.setDisabled(false);
			TxtUserStories.setDisabled(true);
		}
		else
		{
			TxtBugs.setDisabled(true);
			TxtUserStories.setDisabled(true);
		}
	},
	saveChangeRequest: async () => {
		//Status (1 = novo, 2 = Aguardando, 3 = Aprovado )
		//Tipo (1 = Programada, 2 = Nao programada)
		var statusChangeRequest = 1;
		var message;
		if (appsmith.store.typeChangeRequest === 1)
		{
			statusChangeRequest = 3;
			message = "Registro de mudança registrado com sucesso!";
		}
		else if (appsmith.store.typeChangeRequest === 2)
		{
			statusChangeRequest = 2;
			message = "Registro de mudança solicitado com sucesso!";
		}
		let result = await SaveChangeRequest.run({status:statusChangeRequest});

		result = result[0];		
		for(let propName in result) {
			if(result.hasOwnProperty(propName)) {
				var propValue = result[propName];
			}
		};
		result = propValue;
		if (appsmith.store.typeChangeRequest === 1)
		{
			result = SaveAssessement.run({changeRequestId:result});
		};
		closeModal('MdlSave');
		TxtTitleMdlResult.setText(message);
		resetWidget('FrmChangeRequest');
		removeValue('typeChangeRequest');
		removeValue('riskGrade');
		TxtRisk.setText("");
		showModal('MdlResult');
		return (result);
	},
	DefinitionRisk: () => {
		if (Impacto.selectedOptionValue === null || Impacto.selectedOptionValue === '' || Probabilidade.selectedOptionValue === null || Probabilidade.selectedOptionLabel === '')
	{
		TxtRisk.setText("");
		removeValue("riskGrade");
	}
	else
	{
		var riskData = GetRisk.data.filter((word => word.probabilityid === Probabilidade.selectedOptionValue && word.impactid === Impacto.selectedOptionValue)); 
	  let riskGrade = riskData[0].grade;
		let riskDescription = riskData[0].description;
		storeValue("riskGrade", riskGrade);
		TxtRisk.setText(riskDescription);
	}
  },
	statusRisk: () => {
		let risk = TxtRisk.text;
		if (risk === 'Muito baixo') {
			//Azul
			return 'RGB(204,204,255)';
		};
		if (risk === 'Baixo') {
			//verde
			return 'RGB(193, 224, 193)';
		};
		if (risk === 'Médio') {
			//Amarelo
			return 'RGB(255, 255, 153)'
		}
		if (risk === 'Alto') {
			//Vemelho
				return 'RGB(255, 204, 204)'
		}
		if (risk === 'Muito alto') {
			//roxo
			return 'RGB(191, 127, 191)'
		}
		if (risk === '') {
			return 'RGB(255, 255, 255)'
		}
	}
}

