			//Amarelo
			return 'RGB(255, 255, 153)'
		}
		if (risk === 'Alto') {
			//Vemelho
				return 'RGB(255, 204, 204)'
		}
		if (risk === 'Muito alto') {
			//roxo
			return 'RGB(191, 127, 191)'
		}
	},
	onChangeRequestSelected: async () => {
		storeValue('changeRequest', TbChangeRequest.selectedRow);
		showModal('MdlChangeRequestDetail');
	},
	reset: async() => {

		GetChangeRequest.run();
		resetWidget('TbMyChangeRequest', true);
		await this.MyChangeRequest();
	},
}

